function Move(face, power) {
    this.face = face;
    this.power = power;
}

function generate() {
    var list = [];
    for (var face = 0; face < 6; face++) {
        list.push(new Move(face, 1)); // face ^ 1
        list.push(new Move(face, 2)); // face ^ 2
        list.push(new Move(face, 3)); // face ^ -1
    }
    return list;
}

exports.Move = Move;
exports.generate = generate;
